(591, 3403, '_summary_title', 'field_6478ccb5ed74b'),
(592, 3403, 'summary_description', 'A marketing analytics platform was scaling fast without a marketing infrastructure to handle its growth spurt. The company turned to Opascope to build a marketing framework and implement best practices without disrupting business as usual.'),
(593, 3403, '_summary_description', 'field_6478cd70ed74d'),
