(1142, 3419, 'content', '<span style=\"font-weight: 400;\">A lifestyle startup was spending more than 70% of its ad-driven revenue on paid spend. Opascope cut that number in half.</span>'),
(1143, 3419, '_content', 'field_61e84c5130dc0'),
(1144, 3419, 'pdf_link_text', 'Read Now'),
(1145, 3419, '_pdf_link_text', 'field_61e84c6230dc1'),
